// Slideshow.js
import React from 'react';
import Slider from 'react-slick';
import styles from '../style/Slideshow.module.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function Slideshow({ images }) {
    // Slider settings
    const settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
    };

    return (
        <div className={styles.slideshowWrapper}>

            <Slider {...settings}>
                {images.map((image, index) => (
                    <div key={index} style={{ height: '100%', width: '100%', backgroundColor: 'red' }}>
                        {/* Static test content */}
                    </div>
                ))}
            </Slider>
            {/*<Slider {...settings}>
                {images.map((image, index) => (
                    <div key={index} style={{ height: '100%', backgroundColor: 'red' }}>

                        <div style={{ height: '100%', width: '100%', backgroundColor: 'blue' }}>
                            <img src={image.picUrl}
                                 alt={image.movieName}
                                 className={styles.slideshowImage}
                                 onError={(e) => console.error(`Failed to load image at ${e.target.src}`)}
                            />
                        </div>
                    </div>
                ))}
            </Slider>*/}
        </div>
    );
}

export default Slideshow;
