import React, { useState, useEffect } from 'react';
import styles from '../style/AllScreenings.module.css';
import moment from 'moment';

function AllScreenings() {
    const [screenings, setScreenings] = useState([]);
    const [selectedDay, setSelectedDay] = useState('Mon'); // Default day

    useEffect(() => {
        fetch('http://localhost:8080/screenings/allScreenings')
            .then(response => response.json())
            .then(data => {
                // For each screening, get the count of reserved seats
                const screeningsWithSeats = data.map(screening => {
                    return fetch(`http://localhost:8080/reservations/howManyReserved/${screening.screening.id}`)
                        .then(response => response.json())
                        .then(reservedSeats => ({
                            ...screening,
                            reservedSeats: reservedSeats,
                            availableSeats: screening.room.totalSeats - reservedSeats
                        }))
                        .catch(error => console.error('Error fetching reserved seats:', error));
                });

                Promise.all(screeningsWithSeats).then(completed => {
                    setScreenings(completed);
                });
            })
            .catch(error => console.error('Error fetching screenings:', error));
    }, []);

    const filteredScreenings = screenings.filter((screening) =>
        moment(screening.screening.startTime).format('ddd') === selectedDay
    );

    return (
        <div className={styles.allScreenings}>
            <div className={styles.menuBar}>
                {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
                    <button
                        key={day}
                        className={`${styles.dayButton} ${selectedDay === day ? styles.active : ''}`}
                        onClick={() => setSelectedDay(day)}
                    >
                        {day}
                    </button>
                ))}
            </div>
            <div className={styles.screeningsList}>
                {screenings.map((screening) => (
                    <div key={screening.screening.id} className={styles.screeningContainer}>
                        <h2>{screening.movie.movieName}</h2>
                        <p>Rating: {screening.movie.rating}</p>
                        <p>Room: {screening.room.room_name}</p>
                        <p>Start Time: {moment(screening.screening.startTime).format('LLL')}</p>
                        <p>Available Seats: {screening.availableSeats}</p>
                    </div>
                ))}
            </div>
        </div>

)
    ;
}

export default AllScreenings;
