import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import moment from 'moment/moment'; // Import moment.js library
import './ScreeningInfo.css';

function ScreeningInfo() {
    const [screeningInfo, setScreeningInfo] = useState(null);
    const { id } = useParams(); // Accessing path parameters using useParams

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/info/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setScreeningInfo(data);
            })
            .catch((error) => console.error(error));
    }, [id]);

    const formatScreeningTime = (timestampInMilliseconds) => {
        const dateObject = new Date(timestampInMilliseconds);
        return moment(dateObject).format('dddd, HH:mm'); // Use moment.js for formatting
    };

    if (!screeningInfo) {
        return <div>Loading...</div>;
    }

    return (
        <div className="screening-info-container-wrapper"> {/* Create a wrapper container */}
            <div className="screening-info-container">
                <h2>{screeningInfo.movie.movieName}</h2>
                <h3>{formatScreeningTime(screeningInfo.screening.startTime)}, {screeningInfo.room.room_name}</h3>
                <div className="movie-details">
                    <div className="movie-info">
                        <p><b>Director(s):</b> {screeningInfo.movie.directors}</p>
                        <p><b>Rating:</b> {screeningInfo.movie.rating}</p>
                        <p><b>Release Year:</b> {screeningInfo.movie.releaseYear}</p>
                        <p><b>Runtime:</b> {screeningInfo.movie.runTime}</p>
                    </div>
                    <div className="movie-image">
                        <img src={screeningInfo.movie.picUrl} alt={screeningInfo.movie.movieName} />
                    </div>
                </div>
            </div>
            <div className="screening-info-container screening-info-container--right"> {/* Add unique class */}
                <h2>{screeningInfo.movie.movieName} (Right)</h2>  {/* Add "(Right)" to differentiate */}
                <h3>{formatScreeningTime(screeningInfo.screening.startTime)}, {screeningInfo.room.room_name}</h3>
                <div className="movie-details">
                    <div className="movie-info">
                        <p><b>Director(s):</b> {screeningInfo.movie.directors}</p>
                        <p><b>Rating:</b> {screeningInfo.movie.rating}</p>
                        <p><b>Release Year:</b> {screeningInfo.movie.releaseYear}</p>
                        <p><b>Runtime:</b> {screeningInfo.movie.runTime}</p>
                    </div>
                    <div className="movie-image">
                        <img src={screeningInfo.movie.picUrl} alt={screeningInfo.movie.movieName} />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ScreeningInfo;
