import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styles from '../style/ScreeningInfo.module.css';
import moment from "moment/moment";

/*function Seat({ isOccupied, isVip, seatNumber, onHover }) {
    const seatClasses = `${styles.seat} ${isOccupied ? styles.occupied : ''} ${isVip ? styles.vip : ''}`;

    const handleMouseEnter = () => {
        if (onHover) {
            onHover(true);
        }
    };

    const handleMouseLeave = () => {
        if (onHover) {
            onHover(false);
        }
    };

    return (
        <div
            className={seatClasses}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            tabIndex="0"
        >
            <span className={styles.seatNumber}>{seatNumber}</span>
        </div>
    );
}*/

function Seat({ isOccupied, isVip, seatNumber, isRecommended }) {
    const seatClasses = `${styles.seat} ${isOccupied ? styles.occupied : ''} ${isVip ? styles.vip : ''} ${isRecommended ? styles.recommended : ''}`;

    return (
        <div className={seatClasses} tabIndex="0">
            <span className={styles.seatNumber}>{seatNumber}</span>
        </div>
    );
}

function ScreeningInfo() {
    const [screeningInfo, setScreeningInfo] = useState(null);
    const [numberOfReservedSeats, setNumberOfReservedSeats] = useState(null);
    const {id} = useParams();
    const totalSeats = 100;
    const [desiredSeats, setDesiredSeats] = useState(0);
    const [recommendedSeats, setRecommendedSeats] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/info/${id}`)
            .then(response => response.json())
            .then(data => setScreeningInfo(data))
            .catch(error => console.error(error));

        fetch(`http://localhost:8080/reservations/howManyReserved/${id}`)
            .then(response => response.json())
            .then(data => setNumberOfReservedSeats(data))
            .catch(error => console.error(error));
    }, [id]);



    const formatScreeningTime = timestamp => {
        return moment(timestamp).format('LLL'); // Format the date and time as needed
    };

    // Add a method to find and recommend seats
    const findSeats = () => {
        // Reset previous recommendations
        setRecommendedSeats([]);

        // Algorithm to find the best seats (in the middle)
        let bestSeats = [];
        let minDistance = Infinity;
        for (let i = 0; i <= seats.length - desiredSeats; i++) {
            let block = seats.slice(i, i + desiredSeats);
            if (!block.includes(true)) {
                // Calculate distance of the block's center to the center of the seating area
                const blockCenter = i + Math.floor(desiredSeats / 2);
                const distance = Math.abs(blockCenter - totalSeats / 2);
                if (distance < minDistance) {
                    bestSeats = Array.from({ length: desiredSeats }, (_, idx) => i + idx);
                    minDistance = distance;
                }
            }
        }

        // Update state with the best found seats
        setRecommendedSeats(bestSeats);
    };

    if (!screeningInfo || numberOfReservedSeats === null) {
        return <div>Loading...</div>;
    }

    return (
        <div className={styles.screeningInfoBody}>
            <div className={styles.movieContainer}>
                <div className={styles.basicInformation}>
                    <h1 className={styles.movieTitle}> {screeningInfo.movie.movieName} </h1>
                    <h2 className={styles.dateTitle}>{formatScreeningTime(screeningInfo.screening.startTime)}</h2>
                    <h3 className={styles.roomTitle}> {screeningInfo.room.room_name} </h3>
                </div>
                <div className={styles.movieDetails}>
                    <div className={styles.moviePoster}>
                        <img src={screeningInfo.movie.picUrl}/>
                    </div>
                </div>
            </div>
            <SeatingArea occupiedSeatsCount={numberOfReservedSeats} totalSeats={totalSeats}
                         screeningInfo={screeningInfo}/>
            <input
                type="number"
                value={desiredSeats}
                onChange={(e) => setDesiredSeats(Number(e.target.value))}
                placeholder="Number of seats"
            />
            <button onClick={findSeats}>Recommend Seats</button>

            {/* Pass the recommendedSeats to SeatingArea */}
            <SeatingArea occupiedSeatsCount={numberOfReservedSeats} totalSeats={totalSeats}
                         screeningInfo={screeningInfo} recommendedSeats={recommendedSeats}/>
        </div>
    );
}


function SeatingArea({occupiedSeatsCount, totalSeats, screeningInfo}) {
    const [seats, setSeats] = useState([]);

    useEffect(() => {
        let newSeats = Array(totalSeats).fill(false);
        let occupiedIndices = new Set();
        while (occupiedIndices.size < occupiedSeatsCount) {
            let randomIndex = Math.floor(Math.random() * totalSeats);
            if (!occupiedIndices.has(randomIndex)) {
                occupiedIndices.add(randomIndex);
                newSeats[randomIndex] = true;
            }
        }
        setSeats(newSeats);
    }, [occupiedSeatsCount, totalSeats]);

    const rows = 10;
    const cols = 10;

    return (
        <div className={styles.seatingContainer}>
            <div className={styles.seatingInformation}>
                <h1 className={styles.movieTitle}><b> Available seats: </b> {totalSeats - occupiedSeatsCount} / {totalSeats}</h1>
                <h2 className={styles.dateTitle}><b> Basic seat price: </b> {screeningInfo.room.basicSeatPrice} €</h2>
                <h3 className={styles.roomTitle}><b> VIP seat price: </b> {screeningInfo.room.vipSeatPrice} €</h3>
            </div>
            <div className={styles.seatingArea}>
                {Array.from({length: rows}, (_, rowIndex) => {
                    return (
                        <div key={rowIndex} className={styles.row}>
                            {Array.from({length: cols}, (_, colIndex) => {
                                const seatId = rowIndex * cols + colIndex;
                                const isOccupied = seats[seatId];
                                const isVip = rowIndex === rows - 1;
                                const isRecommended = recommendedSeats.includes(seatId);
                                return (
                                    <Seat
                                        key={seatId}
                                        isOccupied={isOccupied}
                                        isVip={isVip}
                                        seatNumber={colIndex + 1}
                                        isRecommended={isRecommended}
                                    />
                                );
                            })}
                            <div className={styles.rowNumber}>{rowIndex + 1}</div>
                        </div>
                    );
                })}
            </div>
            <div className={styles.screen}>Screen</div>
        </div>

    );
}

export default ScreeningInfo;
