import React, { useState, useEffect } from 'react';
import styles from '../style/AllScreenings.module.css';
import moment from 'moment';

function AllScreenings() {
    const [screenings, setScreenings] = useState([]);
    const [selectedDay, setSelectedDay] = useState(moment().format('ddd')); // Default to the current day of the week
    const [selectedGenres, setSelectedGenres] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/screenings/allScreenings')
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                // Assuming your data structure has the genres in a 'genre' field
                const screeningsWithGenres = data.map(screening => {
                    return {
                        ...screening,
                        genres: screening.movie.genre.split(',').map(genre => genre.trim()), // Split the genre string into an array
                        availableSeats: null // We will fill this in after fetching the reserved seats
                    };
                });

                // Fetch the number of reserved seats for each screening and update the screeningsWithGenres
                Promise.all(screeningsWithGenres.map(screening =>
                    fetch(`http://localhost:8080/reservations/howManyReserved/${screening.screening.id}`)
                        .then(res => res.json())
                        .then(numberOfReservedSeats => {
                            return {
                                ...screening,
                                availableSeats: screening.room.totalSeats - numberOfReservedSeats
                            };
                        })
                        .catch(error => {
                            console.error('Error fetching reserved seats:', error);
                            return { ...screening, availableSeats: null }; // Indicate that seats are not available
                        })
                ))
                    .then(updatedScreenings => {
                        setScreenings(updatedScreenings); // Now we set the screenings with reserved seats
                    });
            })
            .catch(error => {
                console.error('Error fetching screenings:', error);
            });
    }, [selectedDay, selectedGenres]);


    // Filter screenings by both day and genre
    const filteredScreenings = screenings.filter(screening => {
        const dayMatch = moment(screening.screening.startTime).format('ddd') === selectedDay;
        const genreMatch = selectedGenres.length === 0 || selectedGenres.some(genre => screening.genres.includes(genre));
        return dayMatch && genreMatch;
    });

    const toggleGenre = (genre) => {
        setSelectedGenres(selectedGenres.includes(genre)
            ? selectedGenres.filter(g => g !== genre) // Remove genre if it's already selected
            : [...selectedGenres, genre]); // Add genre if it's not already selected
    };


    const genreButtons = ['Drama', 'Crime', 'Action', 'Biography', 'History'];

    return (
        <div className={styles.allScreenings}>
            <div className={styles.menuBar}>
                <div className={styles.dayOptions}>
                    {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day) => (
                        <button
                            key={day}
                            className={`${styles.dayButton} ${selectedDay === day ? styles.active : ''}`}
                            onClick={() => setSelectedDay(day)}
                        >
                            {day}
                        </button>

                    ))}
                </div>
                <div className={styles.genreOptions}>
                    {genreButtons.map((genre) => (
                        <button
                            key={genre}
                            className={`${styles.genreButton} ${selectedGenres.includes(genre) ? styles.active : ''}`}
                            onClick={() => toggleGenre(genre)}
                        >
                            {genre}
                        </button>
                    ))}
                </div>
            </div>
            {/*<div className={styles.genreFilter}>
                {['Drama', 'Crime', 'Action', 'Biography', 'History'].map(genre => (
                    <label key={genre}>
                        <input
                            type="checkbox"
                            checked={selectedGenres.includes(genre)}
                            onChange={() => toggleGenre(genre)}
                        />
                        {genre}
                    </label>
                ))}
            </div> */}
            <div className={styles.screeningsList}>
                {filteredScreenings.map(screening => (
                    <div key={screening.screening.id} className={styles.screeningContainer}>
                        <div className={styles.moviePoster} style={{ backgroundImage: `url(${screening.movie.picUrl})` }}>
                            {/* Image will be displayed via background */}
                        </div>
                        <div className={styles.movieInfo}>
                            <h2>{screening.movie.movieName}</h2>
                            <h3>Start Time: {moment(screening.screening.startTime).format('LLL')}</h3>
                            <p>Rating: {screening.movie.rating}</p>
                            <p>Room: {screening.room.room_name}</p>
                            <p>Available Seats: {screening.availableSeats !== null ? `${screening.availableSeats} / ${screening.room.totalSeats}` : 'Loading...'}</p>
                        </div>
                        <div className={styles.buttonGroup}>
                            <button className={`${styles.screeningButton} ${styles.getTicketsButton}`}>Get tickets</button>
                            <button className={styles.screeningButton}>View movie</button>
                            <button className={styles.screeningButton}>Schedule</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default AllScreenings;
