import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styles from '../style/ScreeningInfo.module.css';
import moment from 'moment';

function TicketCounter({ title, price, onCountChange }) {
    const [count, setCount] = useState(0);

    const handleIncrement = () => {
        setCount(count + 1);
        onCountChange(count + 1, title.includes('VIP'));
    };

    const handleDecrement = () => {
        if (count > 0) {
            setCount(count - 1);
            onCountChange(count - 1, title.includes('VIP'));
        }
    };

    return (
        <div className={styles.ticketCounter}>
            <div className={styles.ticketDescription}>
                <span>{title}</span>
                <span className={styles.ticketPriceStyle}>{price} â‚¬</span>
            </div>
            <div className={styles.counterControls}>
                <button className={`${styles.counterControls} ${styles.decrementButton}`} onClick={handleDecrement} disabled={count === 0}>-</button>
                <span>{count}</span>
                <button className={`${styles.counterControls} ${styles.incrementButton}`} onClick={handleIncrement}>+</button>
            </div>
        </div>
    );
}

function Seat({ isOccupied, isVip, seatNumber, onHover, recommended }) {
    const seatClasses = `${styles.seat} ${isOccupied ? styles.occupied : ''} ${isVip ? styles.vip : ''} ${recommended ? styles.recommended : ''}`;

    const handleMouseEnter = () => {
        if (onHover) {
            onHover(true);
        }
    };

    const handleMouseLeave = () => {
        if (onHover) {
            onHover(false);
        }
    };

    return (
        <div
            className={seatClasses}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            tabIndex="0"
        >
            {!isOccupied && (
                <span className={styles.seatNumber}>{seatNumber}</span>
            )}
        </div>
    );
}

function ScreeningInfo() {
    const [screeningInfo, setScreeningInfo] = useState(null);
    const [numberOfReservedSeats, setNumberOfReservedSeats] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/info/${id}`)
            .then(response => response.json())
            .then(data => setScreeningInfo(data))
            .catch(error => console.error('Error fetching screening info:', error));

        fetch(`http://localhost:8080/reservations/howManyReserved/${id}`)
            .then(response => response.json())
            .then(data => setNumberOfReservedSeats(data))
            .catch(error => console.error('Error fetching reserved seats:', error));
    }, [id]);

    const formatScreeningTime = timestamp => {
        return moment(timestamp).format('dddd HH:mm, MMM Do YYYY');
    };

    if (!screeningInfo || numberOfReservedSeats === null) {
        return <div>Loading...</div>;
    }

    return (
        <div className={styles.screeningInfoBody}>
            <div className={styles.movieContainer}>
                <div className={styles.basicInformation}>
                    <h1 className={styles.movieTitle}>{screeningInfo.movie.movieName}</h1>
                    <h2 className={styles.dateTitle}>{formatScreeningTime(screeningInfo.screening.startTime)}</h2>
                    <h3 className={styles.roomTitle}>{screeningInfo.room.room_name}</h3>
                </div>
                <div className={styles.movieDetails}>
                    <img src={screeningInfo.movie.picUrl} alt="Movie Poster" className={styles.moviePoster} />
                </div>
            </div>
            <SeatingArea occupiedSeatsCount={numberOfReservedSeats} totalSeats={100} screeningInfo={screeningInfo} />
        </div>
    );
}

function SeatingArea({ occupiedSeatsCount, totalSeats, screeningInfo }) {
    const [seats, setSeats] = useState(Array(totalSeats).fill(false));
    const [recommendedSeats, setRecommendedSeats] = useState([]);

    useEffect(() => {
        // This should be replaced with actual logic to fetch seat occupancy information
        // Simulating occupied seats for demonstration
        let newSeats = Array(totalSeats).fill(false);
        for (let i = 0; i < occupiedSeatsCount; i++) {
            newSeats[i] = true; // Mark seats as occupied
        }
        setSeats(newSeats);
    }, [occupiedSeatsCount, totalSeats]);

    const handleCountChange = (count, isVip) => {
        const newRecommendedSeats = recommendSeats(count, isVip, seats, 10, 10); // Assuming rows=10, cols=10
        setRecommendedSeats(newRecommendedSeats);
    };

    return (
        <div className={styles.seatingContainer}>
            <div className={styles.ticketSelectionArea}>
                <TicketCounter title="Basic seat" price={screeningInfo.room.basicSeatPrice} onCountChange={handleCountChange} />
                <TicketCounter title="VIP seat" price={screeningInfo.room.vipSeatPrice} onCountChange={handleCountChange} />
            </div>
            <div className={styles.seatingArea}>
                {Array.from({ length: 10 }, (_, rowIndex) => (
                    <div key={rowIndex} className={styles.row}>
                        {Array.from({ length: 10 }, (_, colIndex) => {
                            const seatId = rowIndex * 10 + colIndex;
                            return (
                                <Seat
                                    key={seatId}
                                    isOccupied={seats[seatId]}
                                    isVip={rowIndex === 9} // Assuming last row is VIP
                                    seatNumber={colIndex + 1}
                                    recommended={recommendedSeats.includes(seatId)}
                                />
                            );
                        })}
                    </div>
                ))}
            </div>
        </div>
    );
}

function recommendSeats(desiredCount, isVipOnly, seats, rows, cols) {
    let recommendations = [];
    // Recommendation logic goes here
    // Placeholder: recommend the first few available seats for simplicity
    for (let i = 0; i < seats.length && recommendations.length < desiredCount; i++) {
        if (!seats[i] && (!isVipOnly || Math.floor(i / cols) === rows - 1)) {
            recommendations.push(i);
        }
    }
    return recommendations;
}

export default ScreeningInfo;
