import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styles from './ScreeningInfo.module.css';


function Seat({ isOccupied, isVip, seatNumber, onHover }) {
    const seatClasses = `${styles.seat} ${isOccupied ? styles.occupied : ''} ${isVip ? styles.vip : ''}`;

    const handleMouseEnter = () => {
        if (onHover) {
            onHover(true);
        }
    };

    const handleMouseLeave = () => {
        if (onHover) {
            onHover(false);
        }
    };

    return (
        <div
            className={seatClasses}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            tabIndex="0"
        >
            <span className={styles.seatNumber}>{seatNumber}</span>
        </div>
    );
}

function SeatingArea({ occupiedSeatsCount, totalSeats }) {
    const [seats, setSeats] = useState([]);
    const totalRows = Math.ceil(totalSeats / 10); // Assuming 10 seats per row

    useEffect(() => {
        const newSeats = [];
        for (let i = 0; i < totalRows; i++) {
            const row = [];
            for (let j = 0; j < 10; j++) { // Assuming 10 seats per row
                row.push({
                    isOccupied: Math.random() < (occupiedSeatsCount / totalSeats),
                    isVip: i === totalRows - 1, // VIP row is the last row
                    seatNumber: j + 1,
                });
            }
            newSeats.push(row);
        }
        setSeats(newSeats);
    }, [occupiedSeatsCount, totalSeats]);

    const seatingAreaWidth = 900; // Adjust width as needed

    return (
        <div className={styles.seatingArea} style={{ width: seatingAreaWidth }}>
            {seats.map((row, rowIndex) => (
                <div key={rowIndex} className={styles.row}>
                    <div className={`${styles.rowNumber} ${styles.centered}`}>
                        {totalRows - rowIndex}
                    </div>
                    {row.map((seat, seatIndex) => (
                        <Seat key={`${rowIndex}-${seatIndex}`} {...seat} />
                    ))}
                </div>
            ))}
            <div className={styles.screen} style={{ width: seatingAreaWidth }}>
                Screen
            </div>
        </div>
    );
}

function ScreeningInfo() {
    const [screeningInfo, setScreeningInfo] = useState(null);
    const [numberOfReservedSeats, setNumberOfReservedSeats] = useState(null);
    const { id } = useParams();
    const totalSeats = 100;

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/info/${id}`)
            .then(response => response.json())
            .then(data => setScreeningInfo(data))
            .catch(error => console.error(error));

        fetch(`http://localhost:8080/reservations/howManyReserved/${id}`)
            .then(response => response.json())
            .then(data => setNumberOfReservedSeats(data))
            .catch(error => console.error(error));
    }, [id]);

    if (!screeningInfo || numberOfReservedSeats === null) {
        return <div>Loading...</div>;
    }

    return (
        <div className={styles.screeningInfoBody}>
            <SeatingArea occupiedSeatsCount={numberOfReservedSeats} totalSeats={totalSeats} />
        </div>
    );
}

export default ScreeningInfo;
