import React, { useState, useEffect } from 'react';
import './Guest.css';
import moment from 'moment/moment'; // Import moment.js library
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function GuestPage() {
    const [screeningsData, setScreeningsData] = useState({});

    useEffect(() => {
        fetch('http://localhost:8080/screenings/allScreeningTimesByMovies')
            .then(response => response.json())
            .then(data => {
                setScreeningsData(data);
            })
            .catch(error => console.error(error));
    }, []);

    const formatScreeningTime = (timestampInMilliseconds) => {
        const dateObject = new Date(timestampInMilliseconds);
        const day = moment(dateObject).format('Do'); // Get the day with ordinal suffix (e.g., 1st, 2nd, 3rd)
        const monthYear = moment(dateObject).format('MMMM YYYY'); // Get the month and year
        const time = moment(dateObject).format('HH:mm'); // Get the time
        return `${day} of ${monthYear}, ${time}`; // Concatenate the day, month/year, and time
    };

    return (
        <div className="guest-page">
            <h1>Welcome as Guest</h1>
            <div className="screenings-container">
                {/* Map over screeningsData to display movie information and screening times */}
                {Object.entries(screeningsData).map(([movie, screeningInfos]) => (
                    <div key={movie} className="movie-container">
                        <h3>{movie}</h3>
                        <p>Rating: {movie.rating}</p>
                        <p>Release Year: {movie.releaseYear}</p>
                        <p>Runtime: {movie.runTime}</p>
                        <div className="screening-info">
                            <h4>Screening Times:</h4>
                            <table>
                                <thead>
                                <tr>
                                    <th>Date</th>
                                    <th>Time</th>
                                    <th>Room</th>
                                </tr>
                                </thead>
                                <tbody>
                                {screeningInfos && screeningInfos.length > 0 ? (
                                    screeningInfos.map(screening => (
                                        <tr key={screening.screening.id}>
                                            <td>{formatScreeningTime(screening.screening.startTime)}</td>
                                            <td>{screening.screening.startTime}</td>  {/* You might want to format the time here */}
                                            <td>{screening.room.room_name}</td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="3">No screenings available for this movie.</td>
                                    </tr>
                                )}
                                </tbody>
                            </table>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default GuestPage;
