import React, { useState, useEffect } from 'react';
import './Guest.css';
import moment from 'moment/moment'; // Import moment.js library
import axios from 'axios';

function GuestPage() {
    const [screeningsData, setScreeningsData] = useState({});

    useEffect(() => {
        fetch('http://localhost:8080/screenings/allScreeningTimesByMovies')
            .then(response => response.json())
            .then(data => setScreeningsData(data));
    }, []);

    const formatScreeningTime = (timestampInMilliseconds) => {
        const dateObject = new Date(timestampInMilliseconds);
        return moment(dateObject).format('dddd, HH:mm'); // Use moment.js for formatting
    };

    const fetchMovieDetails = async (movieId) => {
        try {
            const response = await axios.get(`http://localhost:8080/movies/idsearch/${movieId}`);
            return response.data;
        } catch (error) {
            console.error(`Error fetching movie details for ${movieId}:`, error);
            return null; // Handle errors gracefully, e.g., return placeholder data
        }
    };

    return (
        <div className="guest-page">
            <h1>Welcome as Guest</h1>
            <div className="screenings-container">
                {/* Map over screeningsData to display movie details and screening times */}
                {Object.entries(screeningsData).map(([movie, screenings]) => (
                    <div key={movie.id} className="movie-container">
                        <h3>{movie.id} - Movie Details Loading...</h3> {/* Placeholder while fetching */}
                        <div className="screening-info">
                            <h4>Screening Times:</h4>
                            <ul>
                                {/* ... rest of your code for displaying screenings */}
                            </ul>
                        </div>

                        {/* Fetch movie details using movie.id and display them conditionally */}
                        {fetchMovieDetails(movie.id).then((movieDetails) => (
                            <>
                                {movieDetails && ( // Check if movieDetails exists before rendering
                                    <>
                                        <h3>{movieDetails.movieName}</h3>
                                        <p>Rating: {movieDetails.rating}</p>
                                        <p>Release Year: {movieDetails.releaseYear}</p>
                                        <p>Runtime: {movieDetails.runTime}</p>
                                    </>
                                )}
                            </>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default GuestPage;
