import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import ScreeningInfo from './ScreeningInfo';
import GuestPage from './GuestPage';
import LoginPage from './LoginPage';
import GuestPageButton from './GuestPageButton';

function App() {
    return (
        <Router>
            <GuestPageButton /> {/* Include the GuestPageButton component here */}
            <Routes>
                <Route path="/" element={<LandingPage />} />
                <Route path="/guest" element={<GuestPage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/screenings/info/:id" element={<ScreeningInfo />} />
            </Routes>
        </Router>
    );
}

function LandingPage() {
    const navigate = useNavigate();

    const handleGuestButtonClick = () => {
        navigate('/guest');
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>Welcome to HRK's Cinema</h1>
            </header>
            <main className="landing-page">
                <div className="login-options">
                    <div className="login-option">
                        <button className="google-login" onClick={() => navigate('/login')}> Login using Google </button>
                        <p className="description"> This will allow you to access your booking history and preferences. </p>
                    </div>
                    <div className="login-option">
                        <button className="guest-login" onClick={handleGuestButtonClick}> Continue as guest </button>
                        <p className="description"> No login required. </p>
                    </div>
                </div>
            </main>
        </div>
    );
}

export default App;
