import React, { useState, useEffect } from 'react';
import moment from 'moment'; // Import moment.js library

function GuestPage() {
    const [screeningsData, setScreeningsData] = useState({});

    useEffect(() => {
        fetch('http://localhost:8080/screenings/allScreeningTimesByMovies')
            .then(response => response.json())
            .then(data => {
                setScreeningsData(data);
            })
            .catch(error => console.error(error));
    }, []);

    const formatScreeningTime = (timestampInMilliseconds) => {
        const dateObject = new Date(timestampInMilliseconds);
        return moment(dateObject).format('dddd, HH:mm'); // Use moment.js for formatting
    };

    return (
        <div className="guest-page">
            <h1>Welcome as Guest</h1>
            <h2>Screenings:</h2>
            {Object.entries(screeningsData).map(([movieTitle, screeningInfos]) => (
                <div key={movieTitle} className="movie-container">
                    <h3>{movieTitle}</h3>
                    <h4>Screening Times:</h4>
                    <ul>
                        {screeningInfos && screeningInfos.length > 0 ? (
                            screeningInfos.map(screening => (
                                <li key={screening.id}>
                                    {/* Use formatScreeningTime function to convert timestamp */}
                                    <p>{formatScreeningTime(screening.startTime)}</p>
                                </li>
                            ))
                        ) : (
                            <p>No screenings available for this movie.</p>
                        )}
                    </ul>
                </div>
            ))}
        </div>
    );
}

export default GuestPage;
