import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import moment from 'moment/moment'; // Import moment.js library
import styles from './ScreeningInfo.module.css';

// Seat component
function Seat({ isOccupied, isSelected, onHover }) {
    return (
        <div
            className={`seat ${isOccupied ? 'occupied' : ''} ${isSelected ? 'selected' : ''}`}
            onMouseEnter={() => onHover(true)}
            onMouseLeave={() => onHover(false)}
            tabIndex="0" // Make it keyboard accessible
        />
    );
}

// SeatingArea component
function SeatingArea({ occupiedSeats }) {
    const totalSeats = 100;
    const seatsPerRow = 10;

    // Render the seats in a 10x10 grid
    const seatRows = Array.from({ length: totalSeats / seatsPerRow }, (_, rowIndex) => {
        const seats = Array.from({ length: seatsPerRow }, (_, seatIndex) => {
            const seatNumber = rowIndex * seatsPerRow + seatIndex;
            const isOccupied = occupiedSeats.includes(seatNumber);
            return (
                <Seat
                    key={seatNumber}
                    isOccupied={isOccupied}
                    isSelected={false} // Placeholder for selection logic
                    onHover={(hover) => console.log(`Seat ${seatNumber} is ${hover ? 'hovered' : 'not hovered'}`)}
                />
            );
        });
        return <div key={rowIndex} className="seatRow">{seats}</div>;
    });

    return <div className="seatingArea">{seatRows}</div>;
}

function ScreeningInfo() {
    const [screeningInfo, setScreeningInfo] = useState(null);
    const [numberOfReservedSeats, setNumberOfReservedSeats] = useState(null);
    const { id } = useParams(); // Accessing path parameters using useParams
    const totalSeats = screeningInfo?.room?.totalSeats;
    const availableSeats = totalSeats - numberOfReservedSeats;

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/info/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setScreeningInfo(data);
            })
            .catch((error) => console.error(error));

        fetch(`http://localhost:8080/reservations/howManyReserved/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setNumberOfReservedSeats(data);
            })
            .catch((error) => console.error(error));

    }, [id]);



    const formatScreeningTime = (timestampInMilliseconds) => {
        const dateObject = new Date(timestampInMilliseconds);
        const day = moment(dateObject).format('Do'); // Get the day with ordinal suffix (e.g., 1st, 2nd, 3rd)
        const monthYear = moment(dateObject).format('MMMM YYYY'); // Get the month and year
        const time = moment(dateObject).format('HH:mm'); // Get the time
        return `${day} of ${monthYear}, ${time}`; // Concatenate the day, month/year, and time
    };


    if (!screeningInfo || numberOfReservedSeats === null) {
        return <div>Loading...</div>;
    }

    return (
        <div className={styles.screeningInfoBody}>
            <div className={styles.movieInfoContainer}>
                <div className={styles.moviePoster}>
                    <img src={screeningInfo.movie.picUrl} alt={screeningInfo.movie.movieName} />
                </div>
                <div className={styles.movieContent}>
                    <h2 className={styles.movieTitle}>{screeningInfo.movie.movieName}</h2>
                    <div className={styles.movieInfo}>
                        <p><b>Director(s):</b> {screeningInfo.movie.directors}</p>
                        <p><b>Release Year:</b> {screeningInfo.movie.releaseYear}</p>
                        <p><b>IMDb Rating:</b> {screeningInfo.movie.rating}</p>
                        <p><b>Certificate:</b> {screeningInfo.movie.certificate}</p>
                        <p><b>Runtime:</b> {screeningInfo.movie.runTime}</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ScreeningInfo;
