import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ScreeningInfo() {
    const [screeningInfo, setScreeningInfo] = useState(null);
    const { id } = useParams(); // Accessing path parameters using useParams

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/info/${id}`)
            .then(response => response.json())
            .then(data => {
                setScreeningInfo(data);
            })
            .catch(error => console.error(error));
    }, [id]);

    if (!screeningInfo) {
        return <div>Loading...</div>;
    }

    return (
        <div className="screening-info-container">
            <h2>Screening Information</h2>
            <p>Movie Title: {screeningInfo.movie.title}</p>
            <p>Rating: {screeningInfo.movie.rating}</p>
            <p>Release Year: {screeningInfo.movie.releaseYear}</p>
            <p>Runtime: {screeningInfo.movie.runTime}</p>
            <p>Screening Time: {formatScreeningTime(screeningInfo.screening.startTime)}</p>
            <p>Room: {screeningInfo.room.room_name}</p>
        </div>
    );
}

export default ScreeningInfo;
