import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import moment from 'moment';
import '../style/MovieScreenings.css'; // Assuming you have a CSS file for styling

function MovieScreenings() {
    const [movieInfo, setMovieInfo] = useState(null);
    const { id } = useParams(); // This will get the movieId from the URL

    useEffect(() => {
        fetch(`http://localhost:8080/screenings/movie/${id}`)
            .then(response => response.json())
            .then(data => {
                setMovieInfo(data);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, [id]);

    const formatScreeningTime = timestamp => {
        return moment(timestamp).format('LLL'); // Format the date and time as needed
    };

    if (!movieInfo) {
        return <div>Loading...</div>; // Display loading message until data is fetched
    }

    return (
        <div className="movie-screenings">
            <h2>{movieInfo.movie.movieName}</h2>
            {/* Display movie details here */}
            <div className="screenings-list">
                <h3>Screenings:</h3>
                {movieInfo.screenings && movieInfo.screenings.map(screening => (
                    <div key={screening.screening.id} className="screening-item">
                        <p>{formatScreeningTime(screening.screening.startTime)}</p>
                        {/* Add more details and navigation as needed */}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MovieScreenings;
