import React, { useState, useEffect } from 'react';
import '../style/MovieCatalog.css';

function MovieCatalog() {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        // Fetch data from the backend API
        fetch('http://localhost:8080/movies/first10')
            .then(response => response.json())
            .then(data => setMovies(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    // Function to chunk the movies array into groups of 5
    const chunkArray = (arr, size) => {
        return Array.from({ length: Math.ceil(arr.length / size) }, (_, index) => {
            const start = index * size;
            return arr.slice(start, start + size);
        });
    };

    // Chunk the movies array into groups of 5
    const movieGroups = chunkArray(movies, 5);

    return (
        <div className="movie-catalog-wrapper">
            <h2>Movie Catalog</h2>
            {movieGroups.map((group, index) => (
                <div key={index} className="movie-list-row">
                    {group.map(movie => (
                        <div key={movie.id} className="movie">
                            <div className="movie-image">
                                <img src={movie.picUrl} alt={movie.movieName} />
                            </div>
                            <div className="movie-details">
                                <h3>{movie.movieName}</h3>
                                <div className="movie-buttons"> {/* Added wrapper div */}
                                    <button className="movie-screenings">View screenings</button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default MovieCatalog;
