import React, { useState, useEffect } from 'react';
import './Guest.css';
import moment from 'moment/moment'; // Import moment.js library

function GuestPage() {
    const [screeningsData, setScreeningsData] = useState([]);


    useEffect(() => {
        fetch('http://localhost:8080/screenings/allScreeningTimesByMovies')
            .then(response => response.json())
            .then(data => {
                const formattedData = Object.entries(data).map(([movie, screeningInfos]) => ({ movie, allScreenings: screeningInfos }));
                setScreeningsData(formattedData);
            })
            .catch(error => console.error(error));
    }, []);


    const formatScreeningTime = (timestampInMilliseconds) => {
        const dateObject = new Date(timestampInMilliseconds);
        return moment(dateObject).format('dddd, HH:mm'); // Use moment.js for formatting
    };

    return (
        <div className="guest-page">
            <h1>Welcome as Guest</h1>
            <div className="screenings-container">
                {/* Map over screeningsData to display movie information and screening times */}
                {screeningsData.map(movieInfo => (
                    <div key={movieInfo.movie.id} className="movie-container">
                        <h3>{movieInfo.movie.title}</h3>
                        <p>Rating: {movieInfo.movie.rating}</p>
                        <p>Release Year: {movieInfo.movie.releaseYear}</p>
                        <p>Runtime: {movieInfo.movie.runTime}</p>
                        <div className="screening-info">
                            <h4>Screening Times:</h4>
                            <ul>
                                {movieInfo.allScreenings && movieInfo.allScreenings.length > 0 ? (
                                    movieInfo.allScreenings.map(screening => (
                                        <li key={screening.screening.id}>
                                            {/* Use formatScreeningTime function to convert timestamp */}
                                            <p>{formatScreeningTime(screening.screening.startTime)} - Room: {screening.room.room_name}</p>
                                        </li>
                                    ))
                                ) : (
                                    <p>No screenings available for this movie.</p>
                                )}
                            </ul>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default GuestPage;
